using Microsoft.EntityFrameworkCore;using OASv1.Data;var builder = WebApplication.CreateBuilder(args);// Add services to the container.// Learn more about configuring Swagger/OpenAPI at https://aka.ms/aspnetcore/swashbucklebuilder.Services.AddEndpointsApiExplorer();builder.Services.AddSwaggerGen();builder.Services.AddControllersWithViews();builder.Services.AddDbContext<ApplicationDbContext>(options =>{    options.UseSqlite(builder.Configuration.GetConnectionString("DefaultConnection"));});var app = builder.Build();// Configure the HTTP request pipeline.if (app.Environment.IsDevelopment()){    app.UseSwagger();    app.UseSwaggerUI();}app.UseHttpsRedirection();app.MapGet("/produtos", () => "teste");app.Run();